drop database if exists snet0611;
create database if not exists snet0611 character set = utf8mb4;

use snet0611;

drop table if exists users;
create table users(
	id serial primary key, -- bigint unsigned not null auto_increment unique
	firstname varchar(50) not null comment 'Имя пользователя',
	lastname varchar(50) not null comment 'Фамилия пользователя',
	email varchar(120) not null unique comment 'Почта email',
	phone varchar(20) not null unique comment 'Телефон',
	birthday date comment 'День рождения',
	home_town varchar(100) comment 'Домашний город',
	gender enum ('male', 'female') not null comment 'Пол',
	photo_id bigint unsigned comment 'Идентификатор фото',
	created_at datetime default now(),
	updated_at datetime default current_timestamp on update current_timestamp,
	pass char(40) not null comment 'Пароль',
	index phone_idx(phone),
	index users_firstname_lastname_idx(firstname, lastname)
);


drop table if exists messages;
create table messages (
	id serial primary key,
	from_user_id bigint unsigned not null,
	to_user_id bigint unsigned not null,
	message text,
	is_read bool default 0,
	created_at datetime default now(),
	foreign key (from_user_id) references users(id),
	foreign key (to_user_id) references users(id)
);


drop table if exists friend_requests;
create table friend_requests (
	initiator_user_id bigint unsigned not null,
	target_user_id bigint unsigned not null,
	status enum ('requested', 'approved', 'unfriended', 'declined'),
	requested_at datetime default now(),
	confirmed_at datetime default current_timestamp on update current_timestamp,
	primary key(initiator_user_id, target_user_id),
	index(initiator_user_id),
	index(target_user_id),
	foreign key (initiator_user_id) references users(id),
	foreign key (target_user_id) references users(id)
);


drop table if exists communities;
create table if not exists communities(
	id serial primary key,
	user_id bigint unsigned not null comment 'Создатель сообщества',
	name varchar(150),
	created_at datetime default now(),
	index (name),
	foreign key (user_id) references users(id)
);


drop table if exists users_communities;
create table if not exists users_communities(
	user_id bigint unsigned not null,
	community_id bigint unsigned not null,
	primary key (user_id, community_id),
	foreign key (user_id) references users(id),
	foreign key (community_id) references communities(id)
);


drop table if exists posts;
create table if not exists posts (
	id serial primary key,
	user_id bigint unsigned not null,
	post text,
	created_at datetime default current_timestamp,
	updated_at datetime default current_timestamp on update current_timestamp,
	foreign key (user_id) references users(id)
);


drop table if exists comments;
create table if not exists comments (
	id serial primary key,
	user_id bigint unsigned not null,
	post_id bigint unsigned not null,
	comment text,
	created_at datetime default current_timestamp,
	updated_at datetime default current_timestamp on update current_timestamp,
	foreign key (user_id) references users(id),
	foreign key (post_id) references posts(id)
);


drop table if exists photos;
create table if not exists photos (
	id serial primary key,
	filename varchar(200),
	user_id bigint unsigned not null,
	created_at datetime default current_timestamp,
	foreign key (user_id) references users(id)
);


drop table if exists likes_users;
create table if not exists likes_users (
	from_user_id bigint unsigned comment 'Кто поставил лайк',
	to_user_id bigint unsigned comment 'Кому',
	created_at datetime default current_timestamp,
	primary key(from_user_id, to_user_id),
	foreign key (from_user_id) references users(id),
	foreign key (to_user_id) references users(id)
);


drop table if exists likes_photos;
create table if not exists likes_photos (
	user_id bigint unsigned,
	photo_id bigint unsigned,
	created_at datetime default current_timestamp,
	primary key(user_id, photo_id),
	foreign key (user_id) references users(id),
	foreign key (photo_id) references photos(id)
);


drop table if exists likes_posts;
create table if not exists likes_posts (
	user_id bigint unsigned,
	post_id bigint unsigned,
	created_at datetime default current_timestamp,
	primary key(user_id, post_id),
	foreign key (user_id) references users(id),
	foreign key (post_id) references posts(id)
);


