-- select

select * from users; -- выбрать таблицу со всеми колонками

select * from users limit 15; -- выбираем первые 15 строк

select * from users limit 20 offset 15; -- пропускаем первые 15 строк и выбираем следующие 20
select * from users limit 15,20; -- аналог предыдущего, сокращенная запись

select lastname, firstname, phone, email from users; -- выбор определенных столбцов

select lastname, firstname, phone, email from users order by lastname asc; -- сортировка по возрастанию, asc можно не писать, это default
select lastname, firstname, phone, email from users order by lastname desc; -- сортировка по убыванию
select hometown, lastname, firstname, phone, email from users order by hometown, lastname, firstname; -- сортировка по нескольким столбцам
select hometown, lastname, firstname, phone, email from users order by hometown desc, lastname asc, firstname asc; -- сортировка по нескольким столбцам, города по убыванию, имена и фамилии по возрастанию

select 'Hello!'; -- вывод строки, аналог print пайтона
select 3*8; -- можно выполнять мат. операции

select concat(lastname, ' ', firstname) as person, phone from users; -- конкатенация строк с указанием имени колонки, as можно не писать
select concat(lastname, ' ', substring(firstname, 1,1), '.') as person, email from users; -- substring(firstname, 1,1) - обрезаем имя до первого символа

select distinct home_town from users; -- выбираем список городов без повторений

select * from users where hometown = 'Самара'; -- выбор пользователей по городу
select hometown, lastname, firstname from users
	where hometown = 'Самара' or hometown = 'Москва' or hometown = 'Челябинск'; -- выбор по нескольким городам с помощью or
select hometown, lastname, firstname from users 
	where hometown in ('Москва', 'Челябинск', 'Самара'); -- аналог вышенаписанного
select hometown, lastname, firstname, gender from users
	where hometown = 'Москва' or gender = 'male'; -- выбор или всех москвичей или всех мужчин
select hometown, lastname, firstname, gender from users
	where hometown = 'Москва' and gender = 'male'; -- выбор всех москвичей мужчин
select hometown, lastname, firstname from users where hometown != 'Москва'; -- всех немосквичей, != и <> - оба знака неравно

select lastname, firstname, birthday from users where birthday >= '1990-01-01'; -- выбор пользователей младше указанной даты
select lastname, firstname, birthday from users where birthday >= '1990-01-01' and birthday <= '1995-01-01'; -- выбор промежутка
select lastname, firstname, birthday from users where birthday between '1990-01-01' and '1995-01-01'; -- аналог предыдущего

select lastname, firstname from users where lastname like 'По%'; -- выбор пользователей, фамилии которых начинаются на 'По'
select lastname, firstname from users where lastname like '%ко'; -- выбор пользователей, фамилии которых заканчиваются на 'ко'
select lastname, firstname from users where lastname like 'Ши_ко'; -- 1 подчеркивание - пропущен 1 символ
select lastname, firstname from users where lastname like '_____'; -- каждое подчеркивание ровно 1 символ, в данном примере 5 подчеркиваний

select count(*) from users; -- подсчет всех строк таблицы (209)
select count(photo_id) from users; -- подсчет всех строк hoto_id без учета null(100)
select count(distinct hometown) from users; -- подсчет всех уникальных городов без повторений(122)
select hometown, count(*) from users group by hometown; -- группировка по городу и подсчет пользователей в каждом городе
select hometown, count(*) from users group by hometown having count(*) >= 10; -- то же, что и выше, но выбор города, в котором >= 10 пользователей

